sourcesContent:
import {
    Cards,
    Users,
    Columns
} from "../models"
var Router = require('koa-router');
var router = new Router();

const verifyToken = require('../middleware/auth');
const XlsxPopulate = require('xlsx-populate');
router.post('/test/aa', verifyToken, async (ctx, next) => {
    const {
        cardName,
        description,
        // dueDate,
        idColumn,

    } = ctx.request.body
    if (!cardName)
        return ctx.status = 400;
    ctx.body = {
        success: false,
        message: "cardName in correct"

    }
    try {

        const newCard = await Cards.create({
            cardName: cardName,
            description: description,
            createBy: ctx.userId,
            // dueDate: dueDate ? moment(dueDate).format("YYYY-MM-DD") : null,
            idColumn: idColumn



        })

        ctx.status = 200;

        ctx.body = {
            success: true,
            message: "Tạo thẻ công việc thành công",
            data: newCard,


        }


    } catch (error) {
        console.log(error)
        ctx.status = 403;
        ctx.body = {
            success: false,
            message: 'Card fails'
        }
        return;

    }

})
router.put('/test/:id', verifyToken, async (ctx, next) => {
    const {
        cardName,
        description,
        // dueDate,
        idColumn,

    } = ctx.request.body
    if (!cardName) {
        ctx.status = 400;
        ctx.body = {
            success: false,
            message: "cardName in correct"
        }

        return;
    }

    try {

        const updatedCard = await Cards.findByPk(ctx.params.id)
        await updatedCard.update({
            cardName: cardName,
            description: description,
            //dueDate: dueDate ? moment(dueDate).format("YYYY-MM-DD") : null,
            idColumn: idColumn
        })

        ctx.status = 200;

        ctx.body = {
            success: true,
            message: "Update thẻ công việc thành công",



        }


    } catch (error) {
        console.log(error)
        ctx.status = 403;
        ctx.body = {
            success: false,
            message: 'Card fails aa'
        }
        return;

    }

})
router.put('/addCardtocolumn/:id', verifyToken, async (ctx, next) => {
    const {

        idColumn,

    } = ctx.request.body

    try {

        const updatedCard = await Cards.findByPk(ctx.params.id)
        if (!updatedCard) {
            ctx.status = 401;
            ctx.body = {
                success: false,
                message: 'Post k tim thay hoac user not authorrised'
            }
        }
        await updatedCard.update({
            idColumn: idColumn
        })

        ctx.status = 200;

        ctx.body = {
            success: true,
            message: "Add card to cot công việc thành công",



        }


    } catch (error) {
        console.log(error)
        ctx.status = 403;
        ctx.body = {
            success: false,
            message: 'Card fails aa'
        }
        return;

    }

})
router.get('/getAll', verifyToken, async (ctx, next) => {

    try {

        const card = await Cards.findAll({
            // attributes: [""],
            include: [{
                    model: Columns,
                    as: "column_info"
                },

                {
                    model: Users,
                    as: "user_info",
                    attributes: ["id", "userName", "realName", "email", "avatar", "phoneNumber", "createdAt", "updatedAt"]
                }
            ]


        })
        XlsxPopulate.fromBlankAsync()
            .then(workbook => {
                // Modify the workbook.
                workbook.sheet("Sheet1").cell("A1").value("id");
                workbook.sheet("Sheet1").cell("B1").value("cardName");
                workbook.sheet("Sheet1").cell("C1").value("dueDate");
                workbook.sheet("Sheet1").cell("D1").value("description");
                workbook.sheet("Sheet1").cell("E1").value("attachment");
                workbook.sheet("Sheet1").cell("F1").value("comment");
                workbook.sheet("Sheet1").cell("G1").value("createdAt");
                workbook.sheet("Sheet1").cell("H1").value("updatedAt");
                workbook.sheet("Sheet1").cell("I1").value("createBy");
